mapper.select:
SELECT
post_id, board_type, title, content_json,
author_user_id, qna_status, created_at, updated_at
FROM post_tb
WHERE board_type = #{boardType}

    AND (
    title LIKE CONCAT('%', #{keyword}, '%')
    OR content_json LIKE CONCAT('%', #{keyword}, '%')
    )



    AND author_user_id = #{currentUserId}

ORDER BY created_at DESC
LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM post_tb
WHERE board_type = #{boardType}

    AND (
    title LIKE CONCAT('%', #{keyword}, '%')
    OR content_json LIKE CONCAT('%', #{keyword}, '%')
    )


    AND author_user_id = #{currentUserId}
